import "./comicsList.scss";
import uw from "../../resources/img/UW.png";
import xMen from "../../resources/img/x-men.png";
import useMarvelService from "../../services/MarvelService";
import { useEffect, useState } from "react";

const ComicsList = () => {
  const { getAllComics } = useMarvelService();
  const [comicsList, setComicsList] = useState();

  useEffect(() => {
    onRequest();
    // eslint-disable-next-line
  }, []);

  const onRequest = () => {
    getAllComics().then(onComicsListLoaded);
  };

  const onComicsListLoaded = (newCharList) => {
    console.log(newCharList);
    setComicsList(...newCharList);
  };

  function renderItems(arr) {
    const items = arr.map((item, i) => {
      let imgStyle = { objectFit: "cover" };
      if (
        item.thumbnail ===
        "http://i.annihil.us/u/prod/marvel/i/mg/b/40/image_not_available.jpg"
      ) {
        imgStyle = { objectFit: "unset" };
      }

      return (
        <li className="comics__item" tabIndex={0} key={item.id}>
          <a href="/">
            <img
              src={item.thumbnail}
              alt={item.title}
              className="comics__item-img"
            />
            <div className="comics__item-name">{item.title}</div>
            <div className="comics__item-price">`${item.price}$`</div>
          </a>
        </li>
      );
    });

    return <ul className="char__grid">{items}</ul>;
  }

  const items = renderItems(comicsList);

  return (
    <div className="comics__list">
      {items}
      <ul className="comics__grid">
        <li className="comics__item">
          <a href="#">
            <img src={uw} alt="ultimate war" className="comics__item-img" />
            <div className="comics__item-name">
              ULTIMATE X-MEN VOL. 5: ULTIMATE WAR TPB
            </div>
            <div className="comics__item-price">9.99$</div>
          </a>
        </li>
        <li className="comics__item">
          <a href="#">
            <img src={xMen} alt="x-men" className="comics__item-img" />
            <div className="comics__item-name">X-Men: Days of Future Past</div>
            <div className="comics__item-price">NOT AVAILABLE</div>
          </a>
        </li>
        <li className="comics__item">
          <a href="#">
            <img src={uw} alt="ultimate war" className="comics__item-img" />
            <div className="comics__item-name">
              ULTIMATE X-MEN VOL. 5: ULTIMATE WAR TPB
            </div>
            <div className="comics__item-price">9.99$</div>
          </a>
        </li>
        <li className="comics__item">
          <a href="#">
            <img src={xMen} alt="x-men" className="comics__item-img" />
            <div className="comics__item-name">X-Men: Days of Future Past</div>
            <div className="comics__item-price">NOT AVAILABLE</div>
          </a>
        </li>
        <li className="comics__item">
          <a href="#">
            <img src={uw} alt="ultimate war" className="comics__item-img" />
            <div className="comics__item-name">
              ULTIMATE X-MEN VOL. 5: ULTIMATE WAR TPB
            </div>
            <div className="comics__item-price">9.99$</div>
          </a>
        </li>
        <li className="comics__item">
          <a href="#">
            <img src={xMen} alt="x-men" className="comics__item-img" />
            <div className="comics__item-name">X-Men: Days of Future Past</div>
            <div className="comics__item-price">NOT AVAILABLE</div>
          </a>
        </li>
        <li className="comics__item">
          <a href="#">
            <img src={uw} alt="ultimate war" className="comics__item-img" />
            <div className="comics__item-name">
              ULTIMATE X-MEN VOL. 5: ULTIMATE WAR TPB
            </div>
            <div className="comics__item-price">9.99$</div>
          </a>
        </li>
        <li className="comics__item">
          <a href="#">
            <img src={xMen} alt="x-men" className="comics__item-img" />
            <div className="comics__item-name">X-Men: Days of Future Past</div>
            <div className="comics__item-price">NOT AVAILABLE</div>
          </a>
        </li>
      </ul>
      <button className="button button__main button__long">
        <div className="inner">load more</div>
      </button>
    </div>
  );
};

export default ComicsList;
