class MarvelService {
  _basis =
    (_apiKey = getResource =
      async (url) => {
        let res = await fetch(url);

        if (!res.ok) {
          throw new Error(`Could not fetch ${url} status:${res.status}`);
        }

        return await res.json();
      });

  getAllCharacters = () => {
    return this.getResource(
      "https://gateway.marvel.com:443/v1/public/characters?limit=9&offset=210&apikey=7aee92fdbe4f8e13d3e13670a06c2326"
    );
  };

  getCharacter = (id) => {
    return this.getResource(
      `https://gateway.marvel.com:443/v1/public/characters/${id}?&apikey=7aee92fdbe4f8e13d3e13670a06c2326`
    );
  };
}
export default MarvelService;
