        activeItem === item.id
          ? "char__item char__item_selected"
          : "char__item";

      return (
        <li className={className} key={item.id} onClick={this.activeItem}>
          <img src={item.thumbnail} alt={item.name} style={imgStyle} />
          <div className="char__name">{item.name}</div>
        </li>
      );
    });
    return items;
  };

