import "./singleComicsPage.scss";
import Spinner from "../spinner/Spinner";
import useMarvelService from "../../services/MarvelService";
import { useState, useEffect } from "react";
import ErrorMessage from "../ErrorMessage/ErrorMessage";
import { useParams, Link } from "react-router-dom";

const SingleComicPage = () => {
  const { comicId } = useParams();
  const { getSingleComic, loading, error, clearError } = useMarvelService();
  const [comics, setComics] = useState(null);

  useEffect(() => {
    updateComic();
  }, [comicId]);

  const updateComic = () => {
    clearError();
    getSingleComic(comicId).then(onComicLoaded);
  };

  const onComicLoaded = (comics) => {
    setComics(comics);
  };

  const content = !(loading || error) ? <View comics={comics} /> : null;
  const errorMessage = error ? <ErrorMessage /> : null;
  const spinner = loading && !error ? <Spinner /> : null;

  return (
    <div className="single-comic">
      {content}
      {errorMessage}
      {spinner}
    </div>
  );
};

