import "./charInfo.scss";
import { Component } from "react";
import MarvelService from "../../services/MarvelService";
import Skeleton from "../skeleton/Skeleton";

class CharInfo extends Component {
  state = {
    char: {},
    loading: false,
    error: false,
  };
  marvelService = new MarvelService();

  componentDidMount() {
    this.updateChar;
  }

  updateChar = () => {
    const { selectedChar } = this.props;
    if (!selectedChar) {
      return;
    }
    this.onCharLoading();
    this.marvelService
      .getCharacter(selectedChar)
      .then(this.onCharLoaded)
      .catch(this.onError);
  };

  onCharLoaded = (char) => {
    this.setState({ char, loading: false });
  };

  onCharLoading = () => {
    this.setState({
      loading: true,
    });
  };

  onError = () => {
    this.setState({
      loading: false,
      error: true,
    });
  };

  render() {
    const { char } = this.state;
    const item = char ? <Char char={char} /> : <Skeleton />;
    return (
      <div className="char__info">
        {item}

        <div className="char__descr">
          {char.description === ""
            ? "Decription is not Aviable"
            : char.description}
        </div>
        <div className="char__comics">Comics:</div>
        <ul className="char__comics-list">
          <li className="char__comics-item">
            All-Winners Squad: Band of Heroes (2011) #3
          </li>
          <li className="char__comics-item">Alpha Flight (1983) #50</li>
          <li className="char__comics-item">Amazing Spider-Man (1999) #503</li>
          <li className="char__comics-item">Amazing Spider-Man (1999) #504</li>
          <li className="char__comics-item">
            AMAZING SPIDER-MAN VOL. 7: BOOK OF EZEKIEL TPB (Trade Paperback)
          </li>
          <li className="char__comics-item">
            Amazing-Spider-Man: Worldwide Vol. 8 (Trade Paperback)
          </li>
          <li className="char__comics-item">
            Asgardians Of The Galaxy Vol. 2: War Of The Realms (Trade Paperback)
          </li>
          <li className="char__comics-item">Vengeance (2011) #4</li>
          <li className="char__comics-item">Avengers (1963) #1</li>
          <li className="char__comics-item">Avengers (1996) #1</li>
        </ul>
      </div>
    );
  }
}

const Char = ({ char }) => {
  return (
    <div className="char__basics">
      <img src={char.thumbnail} alt={char.name} />
      <div>
        <div className="char__info-name">{char.name}</div>
        <div className="char__btns">
          <a href="/" className="button button__main">
            <div className="inner">homepage</div>
          </a>
          <a href="/" className="button button__secondary">
            <div className="inner">Wiki</div>
          </a>
        </div>
      </div>
    </div>
  );
};

export default CharInfo;
